HELLO: PROCEDURE OPTIONS(MAIN);
   DCL MSG CHAR(20) INIT('WELCOME TO PL/I');
   PUT SKIP LIST(MSG);
END HELLO;

HWPRINT: PROC OPTIONS(MAIN);
   PUT SKIP LIST('HELLO');
   PUT SKIP LIST('WORLD');
END HWPRINT;

SHOWNAME: PROC OPTIONS(MAIN);
   DCL NAME CHAR(10) INIT('IBM Z');
   PUT SKIP LIST(NAME);
END SHOWNAME;

MAINFRAM: PROC OPTIONS(MAIN);
   DCL MSG CHAR(25) INIT('HELLO FROM MAINFRAME');
   PUT SKIP LIST(MSG);
END MAINFRAM;

TWOLINES: PROC OPTIONS(MAIN);
   PUT SKIP LIST('FIRST MESSAGE');
   PUT SKIP LIST('SECOND MESSAGE');
END TWOLINES;
