HELLO: PROC OPTIONS(MAIN);
   DCL MSG CHAR(25) INIT('HELLO FROM MAINFRAME');
   PUT SKIP LIST(MSG);
END HELLO;

HWLINES: PROC OPTIONS(MAIN);
   PUT SKIP LIST('HELLO');
   PUT SKIP LIST('WORLD');
END HWLINES;

SHOWNAME: PROC OPTIONS(MAIN);
   DCL NAME CHAR(10) INIT('IBM Z');
   PUT SKIP LIST(NAME);
END SHOWNAME;

WELCOME: PROC OPTIONS(MAIN);
   DCL MSG CHAR(20) INIT('WELCOME TO PL/I');
   PUT SKIP LIST(MSG);
END WELCOME;

TWOLINES: PROC OPTIONS(MAIN);
   PUT SKIP LIST('FIRST MESSAGE');
   PUT SKIP LIST('SECOND MESSAGE');
END TWOLINES;
